import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api';

// Create axios instance with base configuration
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    console.log(`Making ${config.method?.toUpperCase()} request to ${config.url}`);
    return config;
  },
  (error) => {
    console.error('Request error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => {
    console.log(`Response from ${response.config.url}:`, response.data);
    return response;
  },
  (error) => {
    console.error('Response error:', error);
    if (error.response) {
      // Server responded with error status
      console.error('Error data:', error.response.data);
      console.error('Error status:', error.response.status);
    } else if (error.request) {
      // Request was made but no response received
      console.error('No response received:', error.request);
    } else {
      // Something else happened
      console.error('Request setup error:', error.message);
    }
    return Promise.reject(error);
  }
);

// API service methods
export const apiService = {
  // Dashboard
  getDashboardStats: () => api.get('/dashboard/stats'),
  getRecentActivities: () => api.get('/dashboard/recent-activities'),

  // Students
  getStudents: () => api.get('/students'),
  getStudent: (id) => api.get(`/students/${id}`),
  createStudent: (data) => api.post('/students', data),
  updateStudent: (id, data) => api.put(`/students/${id}`, data),
  deleteStudent: (id) => api.delete(`/students/${id}`),
  searchStudents: (query) => api.get(`/students/search?q=${query}`),
  getStudentsByStatus: (status) => api.get(`/students/status/${status}`),
  getStudentsByCourse: (course) => api.get(`/students/course/${course}`),

  // Fees
  getFees: () => api.get('/fees'),
  getFee: (id) => api.get(`/fees/${id}`),
  createFee: (data) => api.post('/fees', data),
  updateFee: (id, data) => api.put(`/fees/${id}`, data),
  deleteFee: (id) => api.delete(`/fees/${id}`),
  getFeesByStudent: (studentId) => api.get(`/fees/student/${studentId}`),
  getFeesByStatus: (status) => api.get(`/fees/status/${status}`),
  getFeesByType: (type) => api.get(`/fees/type/${type}`),
  processPayment: (feeId, paymentData) => api.post(`/fees/${feeId}/payment`, paymentData),

  // Inquiries
  getInquiries: () => api.get('/inquiries'),
  getInquiry: (id) => api.get(`/inquiries/${id}`),
  createInquiry: (data) => api.post('/inquiries', data),
  updateInquiry: (id, data) => api.put(`/inquiries/${id}`, data),
  deleteInquiry: (id) => api.delete(`/inquiries/${id}`),
  getInquiriesByStatus: (status) => api.get(`/inquiries/status/${status}`),
  respondToInquiry: (id, response) => api.post(`/inquiries/${id}/respond`, response),
};

export default api;
